exports.load = function(cls, cnx) {
	console.log('cls = ' + cls + '; cnx = ' + cnx.toString());
	return {
		operation1 : function(s1, d2, cb) {
			var os = require('os');
			var ifaces = os.networkInterfaces();
			for ( var dev in ifaces) {
				var alias = 0;
				ifaces[dev].forEach(function(details) {
					if (details.family == 'IPv4') {
						if (dev == 'en1') {//en0
							var data = '{"ip":"'+details.address+'","port":"8000","apk":"/Users/jiayq/Documents/Project/ddz_android/bin/ddz.apk"}';
							cb.operation1(data);
							return;
						}
						// console.log(dev + (alias ? ':' + alias : ''),
						// details.address);
						++alias;
					}
				});
			}
		}
	};
}